CREATE TABLE IF NOT EXISTS Workout(id INTEGER PRIMARY KEY NOT NULL, name TEXT NOT NULL, date TEXT NOT NULL, created_at DATETIME NOT NULL DEFAULT (strftime('%Y-%m-%d %H:%M:%S', 'now', 'localtime')));
CREATE TABLE IF NOT EXISTS Exercise(id INTEGER PRIMARY KEY NOT NULL, name TEXT NOT NULL, workout_id INTEGER NOT NULL, FOREIGN KEY (workout_id) REFERENCES Workout(id));
CREATE TABLE IF NOT EXISTS Set(id INTEGER PRIMARY KEY  NOT NULL, reps INTEGER NOT NULL, weight REAL NOT NULL, finished INTEGER DEFAULT 0, warm_up INTEGER DEFAULT 0, exercise_id INTEGER NOT NULL, FOREIGN KEY (exercise_id) REFERENCES Exercise(id));
